var navigation = responsiveNav("#nav", {
        customToggle: "#toggle",
      });

jQuery(window).ready(function($) {
  jQuery('.flexslider').flexslider({
    animation: "slide",
    slideshow: true,
    controlNav: false
  });
});

jQuery(document).ready(function($) {
	$('.sidebar-menu').mobileMenu();
});

jQuery(document).ready(function($) {
  //Initialize for inline images
  $('.pop').magnificPopup({type:'image'});
  //Initialize for wordpress galleries
  $('.gallery').magnificPopup({
    delegate: 'a',
    type: 'image',
    mainClass: 'mfp-img-mobile',
    gallery: {
      enabled: true,
      navigateByImgClick: true,
      preload: [0,1]
      }
  });
});


(function($) {
  
  
// Booking Widget
	
//Datepicker
$('.datepicker').pickadate({
  format: 'yyyy-mm-dd',
  formatSubmit: 'yyyy-mm-dd',
  hiddenName: true,
  close: '',
  weekdaysShort: ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'],
  showMonthsShort: true
});
//End

$('#booker-form').submit(function(e){
  event.preventDefault();  	
  return false;
});
$( "#start" ).change(function() {
	updateQuickBookLink();
	console.log('start changed');
})
$( "#nights" ).change(function() {
	updateQuickBookLink();
	console.log('nights changed');
})

function updateQuickBookLink(){
	console.log('reached function');
	var formUrl = $('#form-url').val();
	var checkIn = $('#start').val();
	var nights  = $('#nights').val();
	var gaID	= '_ga=1.39290475.958067821.1432219802';

	var newUrl  = formUrl + gaID + '&checkin=' + checkIn + '&nights=' + nights + '&currency=GBP';

	$('#quickbooklink').attr("href", newUrl);
}

// End Booking Widget
  
})(jQuery);
